def edit(link):
	link = link[9:]
	cont = 0
	for ch in link:
		if ch != '"': cont+=1
		else: break

	link = link[:cont]
	return link

def editLinks(links):
	newLinks = []
	link = str(links[0])
	newLinks.append(edit(link))
	link = str(links[1])
	newLinks.append(edit(link))
	return newLinks

